/**
 * LANDING PAGE CONTENT EXTRACTOR â€“ OUTPUT CLEANER (UNIVERSAL)
 * Handles both match content and live streaming channels
 */

// -------------------------------------
// 0. Read raw agent output
// -------------------------------------
// In n8n, $input.first().json gives the item.
// Depending on previous node, the LLM output might be in 'output', 'text', or the root.
const inputItem = $input.first().json;
const raw = inputItem.output || inputItem.text || inputItem;

// -------------------------------------
// 1. Robust JSON extraction
// -------------------------------------
function extractJson(input) {
  // Case 1: already an object
  if (input && typeof input === 'object' && !Array.isArray(input)) {
    return input;
  }

  if (typeof input !== 'string') {
    // If it's an array or something else, try to interpret or fail gracefully
    if (Array.isArray(input) && input.length > 0) return input[0];
    throw new Error('Unsupported output type: ' + typeof input);
  }

  // Case 2: ```json fenced block
  const fenced = input.match(/```json\s*([\s\S]*?)\s*```/i);
  if (fenced) {
    return JSON.parse(fenced[1]);
  }

  // Case 3: MATCHES:\n{ ... } or CONTENT:\n{ ... }
  // Fixed typo: (?: instead of (? :
  const contentBlock = input.match(/(?:MATCHES|CONTENT):\s*({[\s\S]*})/i);
  if (contentBlock) {
    return JSON.parse(contentBlock[1]);
  }

  // Case 4: first JSON object in text
  const firstJson = input.match(/{[\s\S]*}/);
  if (firstJson) {
    return JSON.parse(firstJson[0]);
  }

  throw new Error('No JSON block found in agent output');
}

let data;
try {
  data = extractJson(raw);
} catch (err) {
  return {
    error: err.message, // Fixed: err. message -> err.message
    raw_output: raw,
    content_found: 0,
    content: [],
    requires_further_extraction: false
  };
}

// -------------------------------------
// 2. URL validation rules
// -------------------------------------
const INVALID_PATTERNS = [
  /^#$/,
  /^\/$/,
  /^javascript:/i,
  /\/(home|about|contact|privacy|dmca|terms)$/i,
  /\/(login|register|signup|subscribe)$/i,
  /\/(schedule|leagues|categories)$/i,
  /facebook\.com|twitter\.com|instagram\.com|telegram/i,
  /bet|casino|ads/i
];

function isValidContentUrl(url) {
  if (!url || typeof url !== 'string') return false;
  // Fixed: ! url.startsWith -> !url.startsWith
  if (!url.startsWith('http')) return false;

  const lower = url.toLowerCase();
  return !INVALID_PATTERNS.some(pattern => {
    if (pattern instanceof RegExp) {
      return pattern.test(lower);
    }
    return lower.includes(pattern);
  });
}

// -------------------------------------
// 3. Normalize content item
// -------------------------------------
function normalizeContent(item) {
  // Fixed: ! isValidContentUrl -> !isValidContentUrl
  if (!item || !isValidContentUrl(item.url)) return null;

  return {
    url: item.url.trim(), // Fixed: item.url. trim() -> item.url.trim()
    title: item.title || null,
    participants: item.participants || item.teams || null,
    time: item.time || null, // Fixed: item. time -> item.time
    category: item.category || 'general', // Fixed: item.category
    broadcaster: item.broadcaster || null,
    status: item.status || 'live', // Fixed: item. status -> item.status
    confidence: ['high', 'medium', 'low'].includes(item.confidence)
      ? item.confidence
      : 'medium'
  };
}

// -------------------------------------
// 4. Deduplicate & clean
// -------------------------------------
const seen = new Set();
const cleanedContent = [];

for (const item of data.content || []) {
  const norm = normalizeContent(item);
  if (!norm) continue;

  const key = norm.url;
  if (seen.has(key)) continue;

  seen.add(key);
  cleanedContent.push(norm);
}

// -------------------------------------
// 5. Determine content type
// -------------------------------------
const contentTypes = cleanedContent.reduce((acc, item) => {
  const cat = item.category.toLowerCase();
  acc[cat] = (acc[cat] || 0) + 1;
  return acc;
}, {});

// Fixed: Object. keys -> Object.keys
const primaryType = Object.keys(contentTypes).length > 0
  ? Object.keys(contentTypes).reduce((a, b) => 
      contentTypes[a] > contentTypes[b] ? a : b
    )
  : 'general';

// -------------------------------------
// 6. Final output
// -------------------------------------
return {
  content_found: cleanedContent.length,
  primary_content_type: primaryType,
  content_breakdown: contentTypes,
  extraction_method: data.extraction_strategy?.method || 'unknown',
  tools_used: data.extraction_strategy?.tools_used || [],
  navigation_steps: data.extraction_strategy?.navigation_steps || [], // Fixed: . navigation_steps -> .navigation_steps
  content: cleanedContent,
  requires_further_extraction: 
    data.metadata?.requires_further_extraction ?? 
    (cleanedContent.length < 5), // Added parenthesis for clarity
  extraction_confidence: data.analysis_summary?.extraction_confidence || 'medium',
  challenges: data.analysis_summary?.challenges_encountered || [],
  completion_reason: data.metadata?.completion_reason || 'Extraction completed', // Fixed: . completion_reason -> .completion_reason
  notes: data.metadata?.recommendation || null // Fixed: . metadata -> .metadata
};
